mapper.select:
	select b.* from
	(select rownum rn, a.* from
	(select * from product
	
    
    order by pro_sell
    ) a ) b
    
	where b.rn > (#{page}-1)*9 and b.rn <= #{page}*9 and category = #{category}
    
    
    
    
    order by pro_discount
    ) a ) b
    
    
    
    order by pro_sell
    ) a ) b
    
	where b.rn > (#{page}-1)*9 and b.rn <= #{page}*9

	select count(*) from product 
	
    
    category = #{category}
    
     
	order by 1
