mapper.select:
	select b.* from
	(select rownum rn, a.* from
	(select * from product
	
    
    category = #{category}
    order by pro_sell
    
	where b.rn > (#{page}-1)*9 and b.rn <= #{page}*9
    
    
    
    
    pro_discount = #{proDiscount}
    order by pro_discount
    
    
    
    ) a ) b

	select count(*) from product 
	
    
    category = #{category}
    
     
	order by 1
